package week1.day4;

public class DefaultValue {
	
	static int a;
	static boolean b;
	static char c;
	static long l;
	static double d;
	static String s;
	static float f;
	static int arr[];
	static short t;
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Default value of int = " +a);
		System.out.println("Default value of boolean = " +b);
		System.out.println("Default value of character = " +c);
		System.out.println("Default value of long = " +l);
		System.out.println("Default value of double = " +d);
		System.out.println("Default value of String = " +s);
		System.out.println("Default value of float = " +f);
		System.out.println("Default value of array =" +arr);
		System.out.println("Default value of short =" +t);
	}

}
